    /**
     *
     * @param input
     * @return
     */
    public static boolean borrar(String input) {
        PreparedStatement delete = null;
        String deleteString = "DELETE FROM empresa.familiares WHERE nombre LIKE ?";
        int eliminados = 0;

        try {
            delete = con.prepareStatement(deleteString);
            delete.setString(1, input);
            eliminados = delete.executeUpdate();
            mostrar();
            System.out.println("Elimin√°ronse " + eliminados + " filas");
            mostrar();

        } catch (Exception e) {
            System.out.println(e.getMessage());
            return false;
        }
        return true;
    }

    /**
     *
     * @return
     */
    public static boolean mostrar() {
        PreparedStatement select = null;
        ResultSet result = null;
        String selectString = "SELECT * FROM empresa.familiares";

        try {
            select = con.prepareStatement(selectString);
            result = select.executeQuery();
            combo.removeAllItems();
            while (result.next()) {
                combo.addItem(result.getString("nombre"));
            }

        } catch (Exception e) {
            System.out.println(e.getMessage());
            return false;
        }

        return true;
    }

    /**
     *
     * @return
     */
    public static boolean conectar() {
        try {
            String mysqlUrl = "jdbc:mysql://192.168.8.6/empresa";
            String usuario = "root1";
            String clave = "root";
            con = DriverManager.getConnection(mysqlUrl, usuario, clave);

        } catch (Exception ex) {
            System.out.println(ex.getMessage());
            return false;
        }
        return true;
    }

